rule.action:
var RADIX_TREE_INDIRECT_PTR = 1;
var root = arguments[0];
var rnode = root.rnode;
if (!rnode.IsNull()) {
	var addr_low = rnode.AddressLow();
	if (addr_low & RADIX_TREE_INDIRECT_PTR)
		rnode.SetAddressLow(addr_low & ~RADIX_TREE_INDIRECT_PTR);
}
return rnode;

var node = arguments[0];
var height = node.height.toUInt32();
if (height > 1) {
	var slots = node.Member(arguments[1][0]);
	var len = slots.ArrayLength();
	var ret = new Array();
	for (var i = 0; i < len; ++i) {
		var e = slots.ArrayElem(i);
		// Dereference once
		if (e.IsAccessible())
			e.SetAddress(e.toPointer());
		else
			e.SetAddress(0);
		e.ChangeType(node.TypeId());
		ret.push(e);
	}
	return ret;
}
else
	return false;
